(1, 'lincolndu', '', 'lincoln', 'mahmud', 'lincolndu@yahoo.com', '$2y$10$9ulBn/O1qieygWTLYYO7y.NPpSs5ild1KnRkFW5kHyEK/Y/PL4kxO', '/tmp/php4symZv', 'শিক্ষামন্ত্রী নুরুল ইসলাম নাহিদ বলেছেন, ইসলামি আরবি বিশ্ববিদ্যালয়ের অধিভুক্ত মাদ্রাসাগুলোতে আধুনিক বিজ্ঞান প্রযুক্তির বিষয়গুলোও পড়ানো হবে। যেন এখান থেকে ডিগ্রিধারীরা বিজ্ঞ আলেম হওয়ার পাশাপাশি দক্ষ প্রযুক্তিবিদও হতে পারেন।', 0, 2015, 1440554358),
(2, 'mahmuddu', '', 'mahmud', 'lincoln', 'lincolndu@gmail.com', 'mahmuddu', '/tmp/phpagmkVR', 'মন্ত্রী তাঁর বক্তৃতায় বলেন, আলেম সমাজের শত বছরের দাবির পরিপ্রেক্ষিতে ইসলামি আরবি বিশ্ববিদ্যালয় প্রতিষ্ঠা করা হয়েছে। ইসলামের তথাকথিত ধারক বাহক পাকিস্তানি শাসকেরা তাদের দুই যুগের শাসনামলে আলেম সমাজের এই যৌক্তিক দাবির প্রতি সম্মান দেখায়নি। এমনকি আওয়ামী লীগ ক্ষমতায় আসলে ইসলাম চলে যাবে বলে দেশের যেসব রাজনৈতিক দল অপপ্রচার চালায়, তারাও ইসলামি আরবি বিশ্ববিদ্যালয় প্রতিষ্ঠা করেনি। শিক্ষামন্ত্রী বলেন, জাতীয় শিক্ষানীতিতে মাদ্রাসা শিক্ষাকে অত্যন্ত গুরুত্ব দেওয়া হয়েছে।', 0, 2015, 2015),
(3, 'lincolndu1', 'Administrator', 'lincoln', 'mahmud', 'lincolndu1@yahoo.com', '$2y$10$4PhyD10wNqkuI/Pg1ItnxuCVY5Z5iLeUb.yShjqrxDleuHhpgMiqa', '/tmp/phprU3PaP', 'Administrator', 0, 2015, 2015),
